
public class Escape {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("/////////");
		System.out.println("|| \" \" ||");
		System.out.println("||  _  ||");
		System.out.println("\\\\\\\\\\\\\\\\\\");
		
		System.out.println();
		
		System.out.println("  \\/");
		System.out.println(" \\\\//");
		System.out.println("\\\\\\///");
		System.out.println("///\\\\\\");
		System.out.println(" //\\\\");
		System.out.println("  /\\");
		
		System.out.println();
		
		System.out.println("  /\\");
		System.out.println(" //\\\\");
		System.out.println("///\\\\\\");
		System.out.println("\\\\\\///");
		System.out.println(" \\\\//");
		System.out.println("  \\/");
		
		System.out.println();
		
		System.out.println("  /~~~~~~~~\\");
		System.out.println(" /_ _ _ _ _ \\");
		System.out.println("{ _\"_\'_\"_\'_\" }");
		System.out.println(" \\          /");
		System.out.println("  \\~~~~~~~~/");
	}

}
